
public class LearningOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Addition Operator
		
		int num1 = 10, num2 = 15;
		
		int num3 = num1+num2;
		
		System.out.println("Addition of two numbers: "+num3);
		
		//Subtraction
		
		num3 = num1-num2;
		
		System.out.println("Difference of two numbers: "+num3);
		
		//Multiplication
		
		num3 = num1*num2;
		
		System.out.println("Product of two numbers: "+num3);
		
		//Division
		
		num3 = num2/num1;
		
		System.out.println("Quotient of division of num1 and num2: "+num3);
		
		//Modulus Operator
		
		num3 = num3%num1;
		
		System.out.println(num3);

	}

}
