
public class PlusEqualTo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Plus Equal to
		int num1 = 5;
		int num2 = 4;
		
		num1 += 4; //num1 = num1+4
		
		System.out.println(num1);
		
		//Minus equal to
		
		num1 -= 3;	// num1 = num1-3
		System.out.println(num1);
		
		//Multiply Equalto
		
		num1 *= num2; 	//num1 = num1*num2
		System.out.println(num1);
		System.out.println(num2);
		
		//Division equal to
		
		num1 /= num2;	//num1 = num1/num2
		System.out.println(num1);
		
//		Modulus
		
		num1%= num2;	//num1 = num1%num2
		System.out.println(num1);
		

	}

}
