package conditionIfElse;

public class ElseIfCondition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		boolean hasNavjotMoney = false;
		boolean hasKuldeepMoney = false;
		
		if(hasKuldeepMoney) {
			
			System.out.println("We can eat lunch at hotel");
		}
		
		else if(hasNavjotMoney){
			
			System.out.println("We can eat lunch at hotel");
			
		}
		else {
			System.out.println("Go home and eat daal");
		
		}

	}

}
